mapper.update:
update tb_image
set is_delete = 1
where journalist_id = #{id}

update tb_image
set is_delete = 1
where news_id = #{id}

update tb_image
set is_delete = 1
where id = #{id};

mapper.select:
select *
from tb_image
where id = #{imageId} and is_delete = 0;

select *
from tb_image
where news_id = #{newsId} and is_delete = 0;
